import React from "react";
import { useLocation } from "react-router-dom";
import { useState } from "react";
import "./maker.scss";
import axios from "axios";
function Submission() {
  const location = useLocation();
  const postId = location.pathname.split("/")[2];
  const [post, setPost] = useState([]);
  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`/posts/${postId}`);
        setPost(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, [postId]);

  return (
    <div className="container-job">
      <div className="jobs" id="jobs">
        <div className="jobs__item">
          <div className="jobs__column jobs__column--left">
            <img src="./asset/Cloth.png" alt="" className="jobs__img" />

            <div className="jobs__info">
              <span className="jobs__number">JOB ID RECORD # {d.id}</span>
              <span className="jobs__title">
                {post.first_name} {post.last_name}
              </span>
              <p className="jobs__details-item">
                Type of clothing: {post.clothing_type}
              </p>
              <br></br>
              <p className="jobs__details-item">
                Location:{post.state_name} , {post.post_code}
              </p>
              <br></br>
              <p className="jobs__details-item">
                Quotation:{post.num_quotation} submissions{" "}
              </p>
              <br></br>
              <br></br>
              <p className="jobs__details-item">{post.description}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Submission;
