import React, { useEffect } from "react";
import "./maker.scss";
import axios from "axios";
import { useLocation } from "react-router-dom";
import { useState } from 'react'


const Maker = () => {
  const [post ,setPosts] = useState([])
  const location = useLocation()
  const postId = location.pathname.split("/")[2] 
  useEffect(()=>{
    const fetchData = async ()=>{
      try{
        const res = await axios.get(`/posts/`)
        console.log(res.data)
        setPosts(res.data)

      }catch(err){
        console.log(err)
      }
    }
    fetchData()

  },[postId])
  return (
    <div class="container-job">

      <div class="jobs" id="jobs">
        <div class="jobs__item">
          {
            post.map((d)=>{
              <div class="jobs__column jobs__column--left">
              <img 
              src={d.images}
              alt=""
              class="jobs__img"/>
              <div class="jobs__info">
                <span class="jobs__number">JOB NUMBER # {d.id}</span>
                <span class="jobs__title">{d.first_name} {d.last_name}</span>
                <p class="jobs__details-item">Type of clothing&nbsp;&nbsp;: {d.clothing_type}</p><br></br>
                <p class="jobs__details-item">Location&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: {d.state_name} , {d.d_code}</p><br></br>
                <p class="jobs__details-item">Quotation&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: {d.num_quotation} quotes</p><br></br><br></br>
                <p class="jobs__details-item">{post.description}</p>
              </div>
            </div>

            })
          }
        </div>
      </div>
    </div>
  );
};

export default Maker;
