import React, { useEffect } from "react";
import "./maker.scss";
import axios from "axios";
import { useLocation } from "react-router-dom";
import { useState } from 'react'
import ImageBlob from "./ImageBlob";


const Maker = () => {
  const [post ,setPosts] = useState([])
  const location = useLocation()
  const postId = location.pathname.split("/")[2] 
  useEffect(()=>{
    const fetchData = async ()=>{
      try{
        const res = await axios.get(`/posts/`)
        // console.log(res.data)
        setPosts(res.data)

      }catch(err){
        console.log(err)
      }
    }
    fetchData()

  },[postId])
  return (
    <div className="container-job">

      <div className="jobs" id="jobs">

        {post.map((d)=>{


              return <div className="jobs__item">
               <div className="jobs__column jobs__column--left">
                 <ImageBlob
                 src={d.images}
                 alt=""
                 className="jobs__img"/>
                 <div className="jobs__info">
                   <span className="jobs__number">JOB NUMBER # {d.id}</span>
                   <span className="jobs__title">{d.first_name} {d.last_name}</span>
                   <p className="jobs__details-item">Type of clothing&nbsp;&nbsp;: {d.clothing_type}</p><br></br>
                   <p className="jobs__details-item">Location&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: {d.state_name} , {d.post_code}</p><br></br>
                   <p className="jobs__details-item">Quotation&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: {d.num_quotation} submissions </p><br></br><br></br>
                   <p className="jobs__details-item">{d.description}</p>
                 </div>
               </div>
             </div>;
        })}


      </div>
    </div>
  );
};

export default Maker;
