import { React, useEffect } from "react";
import { useLocation } from "react-router-dom";
import { useState } from "react";
import "./maker.scss";
import axios from "axios";
import ReactQuill from "react-quill";
import 'react-quill/dist/quill.snow.css';




function Submission() {
  const [post, setJob] = useState([]);
  const location = useLocation();
  const postId = location.pathname.split("/")[2];
  const [message, setMessage] = useState();
  const [loading , setLoading] = useState(false);
  const [email, setEmail] = useState();


  const submitHandler = async () => {
    axios.get('https://emailvalidation.abstractapi.com/v1/?api_key=ecc93b50bf4c4846905530d3120c534f&email=vittoria908salim@gmail.com')
    .then(response => {
        console.log(response.data);
    })
    .catch(error => {
        console.log(error);
    });
  };


  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`/posts/${postId}`);
        setJob(res.data);

      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, [postId]);

  return (
    <div className="container-job">
      <h2 className="section__title">MEYD.IT</h2>
      <span className="section__subtitle">fashion made especially for you</span>
      <div className="jobs" id="jobs">
        <div className="jobs__item">
          <div className="jobs__column ">
            <img src={`/upload/${post.images}`} className="sample_img "></img>

            <div className="jobs__info">
              <h5 className="home_heading">JOB ID NUMBER # {post.id}</h5>
              {/* <span className="jobs__number">JOB ID RECORD # {post.id}</span> */}

              <span className="job__title">
                {post.first_name} {post.last_name}
              </span>
              <br></br>
              <br></br>

              <div className="job-label-cont">
                <p className="label-job">Type of clothing</p>
                <p className="jobs__details-item jobs__text-item">
                  {post.clothing_type}
                </p>
              </div>
              <div className="job-label-cont">
                <p className="label-job">Budget</p>
                <p className="jobs__details-item jobs__text-item">
                  {post.budget} AUD
                </p>
              </div>
              <div className="job-label-cont">
                <p className="label-job">Email Address</p>
                <p className="jobs__details-item jobs__text-item">
                  {post.email_address}
                </p>
              </div>
              <div className="job-label-cont">
                <p className="label-job">Location</p>
                <p className="jobs__details-item jobs__text-item">
                  {post.street_num} {post.streetname} , {post.state_name}{" "}
                  {post.post_code}
                </p>
              </div>
            </div>
          </div>
          <h4 className="description-label">Description</h4>
          <p className="jobs__details-item">{post.description}</p>
        </div>
        {/* <h2 className="section__title">Submit a request :</h2>   */}

        <section className="job_submission" >
          <h3 className="section__subtitle2">Submit a request</h3>
          <ReactQuill className="editor" theme="snow" onChange= {setMessage} value={message}></ReactQuill>
        </section>
        {/* <button type="email_submit" onClick={submitHandler} className="submit-button">submit</button> */}
        {/* <button onClick={submitHandler}  disabled={loading} type="submit">Submit</button> */}
        {/* <a href="mailto:`{post.email_address}`?subject='Job Request MEYD.IT'&body={message}">Click to Send an Email</a> */}
        <a href="mailto:vittoria908salim@gmail.com?subject='jobrequest&body=hello">Click to Send an Email</a>


      </div>
    </div>
  );
}

export default Submission;
