import { React, useEffect } from "react";
import { useLocation } from "react-router-dom";
import { useState } from "react";
import "./maker.scss";
import axios from "axios";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
function Submission() {
  const [post, setJob] = useState([]);
  const location = useLocation();
  const postId = location.pathname.split("/")[2];

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`/posts/${postId}`);
        setJob(res.data);
        console.log(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, [postId]);

  return (
    <div className="container-job">
      <h2 className="section__title" >MEYD.IT</h2>
        <span className="section__subtitle">fashion made especially for you</span>
      <div className="jobs" id="jobs">
        <div className="jobs__item">
          <div className="jobs__column ">
            <img src={`/upload/${post.images}`} className="sample_img "></img>

            <div className="jobs__info">
              <span className="jobs__number">JOB ID RECORD # {post.id}</span>

              <span className="jobs__title">
                {post.first_name} {post.last_name}
              </span>
              <span className="label-job">Type of clothing</span>
              <p className="jobs__details-item">
                 {post.clothing_type}
              </p>
              <br></br>
              <p className="jobs__details-item">
                Location&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                :{post.street_num} {post.streetname}, {post.state_name} ,{" "}
                {post.post_code}
              </p>
              <br></br>
              <p className="jobs__details-item">
                Quotation&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:{" "}
                {post.num_quotation} submissions{" "}
              </p>
              <br></br>
              <br></br>




            </div>

          </div>
          <span className="section__subtitle description-label">Description</span>
          <p className="jobs__details-item">{post.description}</p>

        </div>
        {/* <h2 className="section__title">Submit a request :</h2>   */}

        <form className="job_submission">     
          <h3 className="section__subtitle2">Submit a request :</h3>  
          <ReactQuill className="editor" theme="snow" value="" onChange="" />
          {/* <button type="submit" className="submit-button">submit</button> */}
        </form>
      </div>
    </div>
  );
}

export default Submission;
