import {React ,useEffect}from "react";
import { useLocation } from "react-router-dom";
import { useState } from "react";
import "./maker.scss";
import axios from "axios";
function Submission() {
  const [post, setJob] = useState([]);
  const location = useLocation();
  const postId = location.pathname.split("/")[2];

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`/posts/${postId}`);
        setJob(res.data);
        console.log(res.data);


      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, [postId]);

  return (
    <div className="container-job">
      <div className="jobs" id="jobs">

            <div className="jobs__item">

              <div className="jobs__column jobs__column--left">




                <div className="jobs__info">
                  <span className="jobs__number">JOB ID RECORD # {post.id}</span>
                  <span className="jobs__title">
                    {post.first_name} {post.last_name}
                  </span>
                  <p className="jobs__details-item">
                    Type of clothing&nbsp;&nbsp;: {post.clothing_type}
                  </p>
                  <br></br>
                  <p className="jobs__details-item">
                    Location&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:
                    {post.street_num} {post.streetname}, {post.state_name} , {post.post_code}
                  </p>
                  <br></br>
                  <p className="jobs__details-item">
                    Quotation&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:{" "}
                    {post.num_quotation} submissions{" "}
                  </p>
                  <br></br>
                  <br></br>
                  <p className="jobs__details-item">{post.description}</p>
                </div>
              </div>
              <div className="button-container">

                  <a href="/submission" className="job_button-sub">
                    <i class="job-but icon_img uil uil-user"></i>

                    <h5 className="job-but">SUBMISSION</h5>
                  </a>

              </div>
            </div>

      </div>
    </div>
  );
}

export default Submission;
