import React from 'react'
import { useState } from 'react'
import {Link} from 'react-router-dom'
import "./Customer.scss"
import axios from 'axios'

const Customer = () => {
  const [inputs, setInputs] =  useState({
    first_name : "",
    last_name:"",
    street_name:"",
    state_name:"",
    post_code:"",
    clothes_type:"",
    budget:"",
    description:"",
    images:"",
    email_add:"",
  })
  const[err,setError] = useState(null)
  const handleChange = e =>{
    setInputs(prev=>({...prev, [e.target.name]:e.target.value}))
  }
  const handleSubmit =async e =>{
    e.preventDefault();
    try{
      const res = await axios.post("/auth/upload", inputs)
      console.log(res)

    }catch(err){
      console.log(err)
      setError(err.response.data)
    }

  }

  console.log(inputs)
  return (
    <section className="customer section" >
    <h2 className="section__title" >MEYD.IT</h2>
    <span className="section__subtitle">fashion made especially for you</span>
    <div className="customer__container container grid">
    <img src="./asset/client.png" alt="" className="customer__img"></img>


        <form className="customer__data">
            <h2 className="home_heading" >Start a job.</h2>
            {/* name */}
            <input type="text" className="form-input" onChange={handleChange} id="first-name" placeholder="First Name"></input>
            <input type="text" className="form-input" onChange={handleChange} id="last-name" placeholder="Last Name"></input>
            <br></br>
            <label for="email_add" class="input-text">Email Address</label><br></br>
            <input type="text" onChange={handleChange} placeholder='Email Address' className="form-input email-address" id="email_add"></input>
            <br></br>
            {/* address */}
            <label for="street_name" class="input-text">Street Name</label><br></br>
            <input type="text" onChange={handleChange} className="form-input address" id = "street_name" placeholder='Street Name' ></input>
            <br></br>
            <input type="text" onChange={handleChange} className="form-input address"  id= "state_name" placeholder="State Name"></input>
            <input type="number" onChange={handleChange} className="form-input address" id = "post_code" placeholder="Post Code"></input>



            {/* clothing type */}
            <label for="clothes-type" class="input-text">Type Of Clothing</label> <br></br>
            <select className="form-input"onChange={handleChange}  id="clothes_type" name="clothing">
              <option value="volvo">Sari/Blouse</option>
              <option value="saab">Dress</option>
              <option value="fiat">Ethnic Wear</option>

            </select>
            <br></br>
            {/* budget */}
            <label for="budget" class="input-text">Budget (optional)</label>
            <input type="number" onChange={handleChange} className="form-input address" id="budget" placeholder='Enter a nominal'></input>
            <br></br>

            {/* description */}
            <label for="description" class="input-text">Description</label><br></br>
            <textarea name="description" onChange={handleChange} className="form-input" id="description" cols="40" rows="5" class="form-input"></textarea><br></br>

            {/* upload image */}
            <label for="images" class="input-text">Upload Sample Images</label><br></br>
            <input type="file" id="images" onChange={handleChange} className="form-input" name="filename" ></input>

            <input type="submit" onClick={handleSubmit} className="customer-button"value="Submit"></input>
            {err && <p>{err}</p>}

        </form>



    </div>

</section> 
  )
}

export default Customer
