import React, { useEffect } from "react";
import "./maker.scss";
import axios from "axios";
import { useLocation } from "react-router-dom";
import { useState } from "react";

const Maker = () => {
  const [post, setPosts] = useState([]);
  const location = useLocation();
  const postId = location.pathname.split("/")[2];
  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`/posts/`);

        setPosts(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, [postId]);
  return (
    <div className="container-job">
      <div className="jobs" id="jobs">
        {post.map((d) => {
          return (
            <div className="jobs__item">
              <div className="jobs__column jobs__column--left">
                <img src="./asset/Cloth.png" alt="" className="jobs__img" />

                <div className="jobs__info">
                  <span className="jobs__number">JOB ID RECORD # {d.id}</span>
                  <span className="jobs__title">
                    {d.first_name} {d.last_name}
                  </span>
                  <p className="jobs__details-item">
                    Type of clothing&nbsp;&nbsp;: {d.clothing_type}
                  </p>
                  <br></br>
                  <p className="jobs__details-item">
                    Location&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:{" "}
                    {d.state_name} , {d.post_code}
                  </p>
                  <br></br>
                  <p className="jobs__details-item">
                    Quotation&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:{" "}
                    {d.num_quotation} submissions{" "}
                  </p>
                  <br></br>
                  <br></br>
                  <p className="jobs__details-item">{d.description}</p>
                </div>
              </div>
              <div className="button-container">
                <div className="job_button-sub">
                  <a href="">
                    <i class="job-but icon_img uil uil-user"></i>

                    <h5 className="job-but">SUBMISSION</h5>
                  </a>


                </div>

                <div className="job_button">
                  <a href="">
                    <i className="job-but icon_img uil uil-info-circle"></i>
                    <h5 className="job-but">JOB INFORMATION</h5>
                  </a>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Maker;
